import React, { Component } from 'react';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import {  Button, Container, Divider, Dropdown, Form, Grid, Header, Input, Segment } from 'semantic-ui-react';
import apiData from '../../data/api-get';
import { scroller } from 'react-scroll';
import ReactQuill from 'react-quill';

class Add extends Component{
    constructor(props) {
        super(props);
        this.state = {
            loading:false, 
            harga:'',
            kategori:[],
            spiner:true
        }
    }
    modules = {
        toolbar : [
            ['bold', 'italic', 'underline', 'strike'],       // toggled buttons
            // ['blockquote', 'code-block'],                    // blocks
            // [{ 'header': 1 }, { 'header': 2 }],              // custom button values
            [{ 'list': 'ordered'}, { 'list': 'bullet' }],    // lists
            // [{ 'script': 'sub'}, { 'script': 'super' }],     // superscript/subscript
            // [{ 'indent': '-1'}, { 'indent': '+1' }],         // outdent/indent
            // [{ 'direction': 'rtl' }],                        // text direction
            // [{ 'size': ['small', false, 'large', 'huge'] }], // custom dropdown
            // [{ 'header': [1, 2, 3, 4, 5, 6, false] }],       // header dropdown
            // [{ 'color': [] }, { 'background': [] }],         // dropdown with defaults
            // [{ 'font': [] }],                                // font family
            [{ 'align': [] }],                               // text align
            // ['clean'],                                       // remove formatting
        ]
    }

    formats = [
        'header', 'font', 'background', 'color', 'code', 'size',
        'bold', 'italic', 'underline', 'strike', 'blockquote',
        'list', 'bullet', 'indent', 'script', 'align', 'direction',
        'link', 'image', 'code-block', 'formula', 'video'
    ]

    componentDidMount(){
        this.scrollTop();
        document.title = 'Tambah Produk | desalase.id'
        fetch(apiData.getKategori)
        .then(response => response.json())
        .then( response=> {
            if(response.error){
                alert('gagal mengambil kategori')
            }else{
                this.setState({kategori:response.data,spiner:false})
            }
        })
        .catch(err=>{
            console.log(err)
        })
    }

    scrollTop(){
        scroller.scrollTo('divider-page', {
            duration: 800,
            delay: 0,
            smooth: "easeInOutQuart",
        });
    }

    render() {
        const settings = {
            dots: true,
            autoplay: false,
            infinite: true,
            slidesToShow: 1,
            slidesToScroll: 1
        };
        const {harga, loading, spiner, kategori} = this.state;
        return (
            <Container>
                <Segment loading={loading} vertical textAlign='left' className='detail-container'>
                    <Form onSubmit=''>
                        <Grid className='detail-group'>
                            <Grid.Column mobile={16} tablet={6} computer={6}>
                                <Segment style={{height:'300px'}} className='detail-image'>
                                    <Form.Field required>
                                        <Input type='file'  />
                                    </Form.Field>
                                </Segment>
                                <Form.Field required>
                                    <Dropdown fluid search selection options={kategori} multiple allowAdditions />
                                </Form.Field>
                            </Grid.Column>
                            <Grid.Column mobile={16} tablet={10} computer={10}>
                                <Header className='detail-deskripsi'>
                                    <Form.Field required>
                                        <Input fluid placeholder='Kopi Bubuk' label='Nama Produk' />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Input fluid placeholder='KWT Muda Mudi' label='Produsen' />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Dropdown loading={spiner} placeholder='Kategori' fluid selection options={kategori} />
                                    </Form.Field>
                                    <Form.Field required>
                                        <ReactQuill 
                                            theme='snow' 
                                            placeholder='Deskripsi'
                                            modules={this.modules}
                                            formats={this.formats}
                                        />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Input pattern="[0-9]*" fluid placeholder="harga" onChange={(e)=>{
                                            const num = (e.target.validity.valid) ? e.target.value : harga
                                            this.setState({harga:num})
                                        }} value={harga} label='Harga' />
                                    </Form.Field>
                                </Header>
                                <Divider/>
                                    <Form.Field required>
                                        <Input placeholder='https://wa.me/xxxxxxxx' fluid label='Link Wa' />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Input fluid placeholder='https://tokopedia.com/xxxx' label='Link Tokopedia' />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Input fluid placeholder='https://shopee.com/xxxx' label='Link Shopee' />
                                    </Form.Field>
                            </Grid.Column>
                        </Grid>
                        <Container className='tags-container'>
                            <Button type='submit' size='big' fluid icon='plus' content='Tambah Produk' color='blue' labelPosition='right'/>
                        </Container>
                    </Form>
                </Segment>
                <Divider className='bootom-detail' />
            </Container>
        );
    }
}

export default Add;
