import React, { Component } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { Breadcrumb, Button, Container, Divider, Dropdown, Form, Grid, Header, Image, Input, Segment, Statistic } from 'semantic-ui-react';
import apiData from '../../data/api-get';
import { scroller } from 'react-scroll';
import ReactQuill from 'react-quill';

class Detail extends Component{
    constructor(props) {
        super(props);
        this.state = {
            loading:true, 
            noDetail:false,
            linktree:[],
            id:0,
            harga:'',
            kategori:[],
            spiner:true,
            tags:[],
            tagSelected:'',
            kategoriSelected:0,
            deskripsi:'',
        }
    }

    componentDidMount(){
        const {id} =  this.props.match.params;
        this.setState({id:id})
        this.scrollTop();
        fetch(apiData.detail+id)
        .then(response => response.json())
        .then( response=> {
            if (response.error) {
                this.setState({noDetail:true, loading:false})
            }else{
                this.setState({ 
                    kategoriSelected:response.id_kategori,
                    deskripsi:response.deskripsi,
                    harga:response.harga.replace(',',''),
                    tags:response.tags,
                    linktree:response.link,
                    loading:false,
                    disabled:false,
                    spiner:false,
                })
                document.title = this.state.data.nama+' - desalase.id'
            }
        })
        .catch(err=>{
            console.log(err)
            alert(err)
        })
        document.title = this.state.data.nama+' | desalase.id'
        fetch(apiData.getKategori)
        .then(response => response.json())
        .then( response=> {
            if(response.error){
                alert('gagal mengambil kategori')
            }else{
                this.setState({kategori:response.data,spiner:false})
            }
        })
        .catch(err=>{
            console.log(err)
        })
        fetch(apiData.getTags)
        .then(response => response.json())
        .then( response=> {
            if(response.error){
                // alert('gagal mengambil tags')
            }else{
                this.setState({tags:response.data,spiner:false})
            }
        })
        .catch(err=>{
            console.log(err)
        })
    }

    scrollTop(){
        scroller.scrollTo('divider-page', {
            duration: 800,
            delay: 0,
            smooth: "easeInOutQuart",
        });
    }

    handleHapus=(id)=>{
        var conf = window.confirm('Anda Yakin menghapus produk')
        if (conf) {
            this.setState({loading:true})
            fetch(apiData.hapusproduk+'?id='+id)
            .then(response => response.json())
            .then( response=> {
                if(!response.error){
                    alert('berhasil')
                    document.location.reload()
                }else{
                    alert('error - '+response.msg)
                }
                this.setState({loading:false})
            })
            .catch(err=>{
                alert(err)
                console.log(err)
                this.setState({loading:false})
            })
        }
    }

    render() {
        const settings = {
            dots: true,
            autoplay: false,
            infinite: true,
            slidesToShow: 1,
            slidesToScroll: 1
        };
        const {
            loading,
            spiner,
            id,
            kategori,
            harga,
            linktree,
            deskripsi,
            disabled,
            tags,
            kategoriSelected,
            data, 
            noDetail
        } = this.state;
        return (
            <Container>
                {noDetail ? 
                <Statistic className='no-produk-msg' label='Tidak ada produk yang dapat ditampilkan' value='Tidak Tersedia' text /> 
                :
                <Segment loading={loading} vertical textAlign='left' className='detail-container'>
                    <Form>
                        <Grid className='detail-group'>
                            <Grid.Column mobile={16} tablet={6} computer={6}>
                                <Segment style={{height:'300px'}} className='detail-image'>
                                    <Form.Field required>
                                        <Input disabled={disabled} name='img'  multiple input={{accept:"image/x-png,image/gif,image/jpeg"}} type='file'  />
                                    </Form.Field>
                                    <Form.Field style={{}} required>
                                        <Input disabled name='id' value={id}/>
                                    </Form.Field>
                                </Segment>
                                <Form.Field required>
                                    <Dropdown disabled={disabled} loading={spiner} placeholder='tags' fluid search selection options={tags} multiple allowAdditions
                                        onChange={(e,value)=>{
                                            var tmp=''
                                            for(let i = 0;i<value.value.length;i++){
                                                tmp=tmp+value.value[i]
                                                if(i!==value.value.length-1){
                                                    tmp = tmp+'|'
                                                }
                                            }
                                            this.setState({tagSelected:tmp})
                                        }}
                                        onAddItem={(e,d)=>{
                                            const tmp = tags
                                            tmp.push({key:d.value,text:d.value,value:d.value})
                                            this.setState({tags:tmp})
                                        }}
                                        value={tags}
                                    />
                                </Form.Field>
                            </Grid.Column>
                            <Grid.Column mobile={16} tablet={10} computer={10}>
                                <Header className='detail-deskripsi'>
                                    <Form.Field required>
                                        <Input disabled={disabled} defaultValue={data.nama} name='nama' fluid placeholder='Kopi Bubuk' label='Nama Produk' />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Input disabled={disabled} defaultValue={data.produsen}  fluid name='produsen' placeholder='KWT Muda Mudi' label='Produsen' />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Dropdown 
                                            onChange={(e,value)=>{this.setState({kategoriSelected:value.value})}}
                                            name='kategori' 
                                            value={kategoriSelected}
                                            loading={spiner} 
                                            placeholder='Kategori' 
                                            fluid 
                                            selection 
                                            options={kategori} 
                                            disabled={disabled} 
                                        />
                                    </Form.Field>
                                    <Form.Field required>
                                        <ReactQuill 
                                            name='deskripsi'
                                            theme='snow' 
                                            placeholder='Deskripsi'
                                            onChange={(e)=>this.setState({deskripsi:e})}
                                            value={deskripsi}
                                            modules={this.modules}
                                            formats={this.formats}
                                        />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Input disabled={disabled} name='harga' pattern="[0-9]*" fluid placeholder="harga" onChange={(e)=>{
                                            const num = (e.target.validity.valid) ? e.target.value : harga
                                            this.setState({harga:num})
                                        }} value={harga} label='Harga' />
                                    </Form.Field>
                                </Header>
                                <Divider/>
                                    <Form.Field required>
                                        <Input disabled={disabled} defaultValue={linktree[0]} name='wa' placeholder='https://wa.me/xxxxxxxx' fluid label='Link Wa' />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Input disabled={disabled} defaultValue={linktree[1]} name='tokped' fluid placeholder='https://tokopedia.com/xxxx' label='Link Tokopedia' />
                                    </Form.Field>
                                    <Form.Field required>
                                        <Input disabled={disabled} defaultValue={linktree[2]} name='shopee' fluid placeholder='https://shopee.com/xxxx' label='Link Shopee' />
                                    </Form.Field>
                            </Grid.Column>
                        </Grid>
                        <Container className='tags-container'>
                            <Button disabled={disabled} loading={disabled} type='submit' size='big' fluid icon='plus' content='Tambah Produk' color='blue' labelPosition='right'/>
                        </Container>
                    </Form>
                </Segment>}
                <Divider className='bootom-detail' />
            </Container>
        );
    }
}

export default Detail;
