import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Button, Container, Dimmer, Divider, Form, Grid, Header, Input, Label, List, Loader } from 'semantic-ui-react';
import apiData from '../../../data/api-get';

class Footer extends Component{
    constructor(props) {
        super(props);
        this.state ={
            footerData: [],
            isLoading: true,
            disabledButton: false,
            loading:false
        }
    }

    componentDidMount(){
        fetch(apiData.footer)
        .then(response => response.json())
        .then( response=> {
            this.setState({footerData:response, isLoading:false})
        })
        .catch(err=>{
            console.log(err)
        })
    }

    handleTambahToko=(e)=>{
        if(e.target.icon.value==='')e.target.icon.focus()
        else if(e.target.header.value==='')e.target.header.focus()
        else if(e.target.data.value==='')e.target.data.focus()
        else{
            this.setState({disabledButton:true,loading:true})
            var dataBody = {
                'icon' : e.target.icon.value,
                'header' : e.target.header.value,
                'data' : e.target.data.value
            }
            fetch(apiData.tambahToko,{
                method:'post',
                headers: {'Content-Type': 'application/json'},
                body : JSON.stringify(dataBody)
            })
            .then(response => response.json())
            .then( response=> {
                if(!response.error){
                    alert('berhasil')
                    console.log(response.msg)
                    document.location.reload()
                }else{
                    alert('error - '+response.msg)
                }
                this.setState({disabledButton:false,loading:false})
            })
            .catch(err=>{
                alert(err)
                console.log(dataBody)
                this.setState({disabledButton:false,loading:false})
            })
        }
    }

    render() {
        const {footerData, isLoading} = this.state;
        return (
            <Container fluid className='footer-container'>
                {isLoading ? 
                <Dimmer inverted active={isLoading}>
                    <Loader size='huge' inline indeterminate/>
                </Dimmer> :
                <Grid>
                    <Grid.Row>
                        <Grid.Column mobile={16} tablet={16} computer={6}>
                            <Grid.Row>
                                <Header inverted as='h1'>Desalase.id</Header>
                                <Divider inverted/>
                            </Grid.Row>
                            {footerData.alamat.map((data, i) => (
                                <Grid.Row key={i} as={Link} to={data.link}>
                                    <Form>
                                        <Input defaultValue={data.icon} label={{ icon: data.icon }} labelPosition='left corner' size='mini' style={{width:'30%'}} focus />
                                        <Input defaultValue={data.header} label={{ icon: 'text width' }} labelPosition='left corner' size='mini' style={{width:'70%'}} focus />
                                        <Input defaultValue={data.data} label='subHeader' size='mini' fluid focus />
                                        <Button.Group size='mini' fluid>
                                            <Button color='yellow' type='submit' content='Simpan' size='mini' />
                                            <Button color='red' content='Hapus' size='mini' />
                                        </Button.Group>
                                    </Form>
                                    <Divider inverted/>
                                </Grid.Row>
                            ))}
                            <Grid.Row>
                                <Form>
                                    <Input placeholder='icon' label={{ icon: 'plus' }} labelPosition='left corner' size='mini' style={{width:'30%'}} focus />
                                    <Input placeholder='header' label={{ icon: 'text width' }} labelPosition='left corner' size='mini' style={{width:'70%'}} focus />
                                    <Input placeholder='deskripsi' label='subHeader' size='mini' fluid focus />
                                    <Button color='green' type='submit' content='Simpan' size='mini' fluid />
                                </Form>
                            </Grid.Row>
                        </Grid.Column>

                        <Grid.Column mobile={16} tablet={16} computer={5}>
                            <Grid.Row>
                                <Header inverted as='h1'>Toko Online</Header>
                                <Divider inverted/>
                            </Grid.Row>
                            {footerData.toko.map((data, i) => (
                                <Grid.Row key={i} as={Link} to={data.link}>
                                    <Form>
                                        <Input defaultValue={data.icon} label={{ icon: data.icon }} labelPosition='left corner' size='mini' style={{width:'30%'}} focus />
                                        <Input defaultValue={data.header} label={{ icon: 'text width' }} labelPosition='left corner' size='mini' style={{width:'70%'}} focus />
                                        <Input defaultValue={data.data} label='subHeader' size='mini' fluid focus />
                                        <Button.Group size='mini' fluid>
                                            <Button color='yellow' type='submit' content='Simpan' size='mini' />
                                            <Button color='red' content='Hapus' size='mini' />
                                        </Button.Group>
                                    </Form>
                                    <Divider inverted/>
                                </Grid.Row>
                            ))}
                            <Grid.Row>
                                <Form onSubmit={this.handleTambahToko}>
                                    <Input name='icon' placeholder='icon' label={{ icon: 'plus' }} labelPosition='left corner' size='mini' style={{width:'30%'}} focus />
                                    <Input name='header' placeholder='header' label={{ icon: 'text width' }} labelPosition='left corner' size='mini' style={{width:'70%'}} focus />
                                    <Input name='data' placeholder='deskripsi' label='subHeader' size='mini' fluid focus />
                                    <Button color='green' type='submit' content='Simpan' size='mini' fluid />
                                </Form>
                            </Grid.Row>
                        </Grid.Column>

                        <Grid.Column mobile={16} tablet={16} computer={5}>
                            <Grid.Row>
                                <Header inverted as='h1'>Peta Situs</Header>
                                <Divider inverted/>
                            </Grid.Row>
                                <Grid.Row as={Link} to='#'>
                                    <List inverted as={Header}>
                                        <List.Item as={Link} to='/'>Beranda</List.Item>
                                        <List.Item as={Link} to='/etalase'>Etalase</List.Item>
                                        <List.Item as={Link} to='blog'>Blog{' '}<Label color='red' size='mini' tag>Comming soon</Label></List.Item>
                                        <List.Item as={Link} to='/tentang'>Tentang</List.Item>
                                    </List>
                                </Grid.Row>
                        </Grid.Column>

                    </Grid.Row>
                </Grid>
                }
            </Container>
        );
    }
}

export default Footer;
