import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Container, Dimmer, Divider, Grid, Header, Icon, Input, Label, List, Loader } from 'semantic-ui-react';
import apiData from '../../../data/api-get';

class Footer extends Component{
    constructor(props) {
        super(props);
        this.state ={
            footerData: [],
            isLoading: true 
        }
    }

    componentDidMount(){
        fetch(apiData.footer)
        .then(response => response.json())
        .then( response=> {
            this.setState({footerData:response, isLoading:false})
        })
        .catch(err=>{
            console.log(err)
        })
    }

    render() {
        const {footerData, isLoading} = this.state;
        return (
            <Container fluid className='footer-container'>
                {isLoading ? 
                <Dimmer inverted active={isLoading}>
                    <Loader size='huge' inline indeterminate/>
                </Dimmer> :
                <Grid>
                    <Grid.Row>
                        <Grid.Column mobile={16} tablet={16} computer={6}>
                            <Grid.Row>
                                <Header inverted as='h1'>Desalase.id</Header>
                                <Divider inverted/>
                            </Grid.Row>
                            {footerData.alamat.map((data, i) => (
                                <Grid.Row key={i} as={Link} to={data.link}>
                                    <Input size='mini' />
                                    <Input size='mini' />
                                    {/* <Header inverted as='h4'>
                                        <Icon name={data.icon}/>

                                        <Header.Content>
                                            {data.header}
                                            <Header.Subheader>
                                                {data.data}
                                            </Header.Subheader>
                                        </Header.Content>
                                    </Header> */}
                                    <Divider inverted/>
                                </Grid.Row>
                            ))}
                        </Grid.Column>

                        <Grid.Column mobile={16} tablet={16} computer={5}>
                            <Grid.Row>
                                <Header inverted as='h1'>Toko Online</Header>
                                <Divider inverted/>
                            </Grid.Row>
                            {footerData.toko.map((data, i) => (
                                <Grid.Row key={i} as={Link} to={data.link}>
                                    <Header inverted as='h4'>
                                        <Icon name={data.icon}/>
                                        <Header.Content>
                                            {data.header}
                                            <Header.Subheader>
                                                {data.data}
                                            </Header.Subheader>
                                        </Header.Content>
                                    </Header>
                                    <Divider inverted/>
                                </Grid.Row>
                            ))}
                        </Grid.Column>

                        <Grid.Column mobile={16} tablet={16} computer={5}>
                            <Grid.Row>
                                <Header inverted as='h1'>Peta Situs</Header>
                                <Divider inverted/>
                            </Grid.Row>
                                <Grid.Row as={Link} to='#'>
                                    <List inverted as={Header}>
                                        <List.Item as={Link} to='/'>Beranda</List.Item>
                                        <List.Item as={Link} to='/etalase'>Etalase</List.Item>
                                        <List.Item as={Link} to='blog'>Blog{' '}<Label color='red' size='mini' tag>Comming soon</Label></List.Item>
                                        <List.Item as={Link} to='/tentang'>Tentang</List.Item>
                                    </List>
                                </Grid.Row>
                        </Grid.Column>

                    </Grid.Row>
                </Grid>
                }
            </Container>
        );
    }
}

export default Footer;
